@use "../abstracts" as *;
@use "../base" as *;
@use "../base/base" as *;


.allRecipesCards {
  display: flex;
  justify-content: space-between;
  margin: auto;
  flex-wrap: wrap;
  position: absolute;
  max-width: 1240px;
  top: 23rem;
}
.card {
  border-radius: 0.3rem;
  overflow: hidden;
  max-width: 23.75rem;
  margin-bottom: 2rem;
  &__anchor {
    cursor: pointer;
  }
  &__bg {
    height: 178px;
    background-color: $color-background;
  }
  &__recipe {
    display: flex;
    flex-direction: column;
    height: 186px;
    overflow: hidden;
    padding: 1.2rem;
    padding-top: 1rem;
    background-color: $color-background2;
  }
  &__title {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 1.13rem;
    font-weight: 400;
    white-space: nowrap;
    line-height: 1.4rem;
    &__h3 {
      overflow: hidden;
      text-overflow: ellipsis;
    }
    &__time {
      display: flex;
      justify-content: space-between;
      align-items: center;
      align-self: center;
      font-weight: 700;
    }
    &__clock {
      margin-right: 0.5rem;
      font-weight: 400;
    }
  }
  &__content {
    display: flex;
    justify-content: space-between;
    align-content: center;
    overflow: hidden;
    text-overflow: ellipsis;
    margin-top: 1.5rem;
  }
  &__ingredients {
    font-size: 0.75rem;
    width: 40%;
  }
  &__description {
    font-family: $font-recipes;
    hyphens: auto;
    font-size: 0.75rem;
    height: 109px;
    line-height: 0.9rem;
    width: 50%;
  }
}

.ingredientsList {
  display: flex;
  overflow: hidden;
  // flex-wrap: wrap;
  white-space: nowrap;
  font-size: 0.75rem;
  line-height: 0.9rem;
  &__item {
    font-weight: 700;
  }
  &__quantity {
    overflow: hidden;
    text-overflow: ellipsis;
  }
  &__units {
    overflow: hidden;
    text-overflow: ellipsis;
  }
}

